mapper.insert:
INSERT INTO BOARD(
                    CATEGORY
                  , TITLE
                  , CONTENT
                  , REG_TIME
                  , REG_ID
                  , MODIFY_YN
)
    VALUES(
                #{category}
              , #{title}
              , #{content}
              , NOW()
              , #{regId}
              , 'N'
          )

mapper.update:
UPDATE BOARD
SET CATEGORY = #{category}
  , TITLE = #{title}
  , CONTENT = #{content}
  , MODIFY_YN = 'Y'
  , MODIFY_TIME = NOW()
WHERE SEQ = #{seq}
