mapper.insert:
insert into MEMBERS(
                      ID
                    , PASSWORD
                    , NAME
                    , BUSINESS_CATEGORY
                    , BUSINESS_REG_NUM
                    , OPEN_TIME
                    , JOIN_TIME
)
values(
          #{id}
        , #{password}
        , #{name}
        , #{businessCategory}
        , #{businessRegNum}
        , STR_TO_DATE(#{openTime},'%Y%m%d')
        , NOW()
      )

mapper.select:
SELECT COUNT(id)
  FROM MEMBERS
 WHERE ID = #{id}

SELECT COUNT(id)
  FROM MEMBERS
 WHERE ID = #{id}
   AND PASSWORD = #{password}
